mapper.insert:

	select 'BOQNA' || LPAD(seq_qna.nextval, 5, '0') from dual

insert into board(
	board_no, writer, title, content, wrt_dt, board_stat_code, hit
)values(
	#{boardNo}, #{writer}, #{title}, #{content}, sysdate, 'TEST000', 0
)

mapper.select:
		select
 		  b.*
 		from (
	 	  select 
	 	  	a.*, row_number() over (order by a.board_no desc) rnum
	 	  from(
	 		select
				board_no, title, content, writer, wrt_dt, hit
			from board
			where board_no like 'BOQNA'||'%'

	 		order by board_no desc
	 	  ) a
	 	) b

	 		where b.rnum >= #{startRow} and b.rnum <= #{endRow}
